    public String truncateSentence(String s, int k) {
        String res = "";
        for (int i = 0; i < s.length(); i++) {
            if (k == 0) break;
            if (Character.isSpaceChar(s.charAt(i))) k--;
            res += s.charAt(i);
        }
        return res;
    }

