    public int maxPower(String s) {
        if (s.length() == 0) return 0;
        int res = 1;
        int tempNum = 1;
        String p = String.valueOf(s.charAt(0));
        for (int i = 1; i < s.length(); i++) {
            final String s1 = String.valueOf(s.charAt(i));
            if (!p.equals(s1)) {
                p = s1;
                tempNum = 1;
            } else {
                tempNum++;
            }
            res = Math.max(tempNum, res);
        }
        return res;
    }

